import React from "react";
import "./module.days.css";
import Forecast from "../Forecast";
import { useLocation } from "react-router-dom";

const Days = () => {
  const findIcon = (condition) => {
    let icon;
    switch (condition) {
      case "Clear":
        icon = "fa-cloud";
        break;
      case "Sunny":
        icon = "fa-sun";
        break;
      case "Mist":
        icon = "fa-cloud-meatball";
        break;
      case "Cloudy":
        icon = "fa-cloud";
        break;
      case "Partly cloudy":
        icon = "fa-cloud-sun";
        break;
      case "Overcast":
        icon = "fa-smog";
        break;
      case "Blizzard":
        icon = "fa-meteor";
        break;
      case "Fog":
        icon = "fa-smog";
        break;
      case "Light rain":
        icon = "fa-cloud-rain";
        break;
      case "Medium rain":
        icon = "fa-cloud-rain";
        break;
      case "Heavy rain":
        icon = "fa-cloud-showers-heavy";
        break;
      case "Light snow":
        icon = "fa-snowflake";
        break;
      case "Medium snow":
        icon = "fa-snowflake";
        break;
      case "Heavy snow":
        icon = "fa-icicles";
        break;
      default:
        icon = "fa-cloud";
    }
    return icon;
  };
  const dateToWords = (date) => {
    const months = [
      "Jan",
      "Feb",
      "Mar",
      "Apr",
      "May",
      "Jun",
      "Jul",
      "Aug",
      "Sep",
      "Oct",
      "Nov",
      "Dec",
    ];

    date = new Date(date);
    const month = months[date.getMonth()];
    const dateNum = date.getDate();

    return `${month} ${dateNum}`;
  };

  const location = useLocation();

  console.log(location);

  let datas = location?.state?.data ? location?.state?.data : [];

  return (
    <div className="mainBox">
      <div className="currentt">
        <h1 className="current-temp">
          {/* {weatherData?.temp} */}
          <sup>7 days forecast</sup>
        </h1>

        {datas?.map((day, i) => {
          return (
            i < 3 && (
              <Forecast
                key={day.date}
                date={dateToWords(day.date)}
                icon={findIcon(day.day?.condition?.text)}
                value={day.day?.avghumidity}
              />
            )
          );
        })}
      </div>
    </div>
  );
};

export default Days;
